$(function(){
	$('#allEntries').before($('#pagesBlock1').clone())
	$('#side_left').find('.blacknav').eq(0).after('<hr><style>div[id^="entryID"]{position:relative}a.entryLink{position:absolute;top:70px;font-size:20px;font-style:Tahoma;width:570px;text-overflow: ellipsis;overflow: hidden;white-space: nowrap;}span[id^="spanload]{position:absolute;top:100px;left:100px;}span[id^="spanload"] img{width:30px}div[id^="lnks"]{position:absolute;top:130px;left:70px;}#hided{height: 350px;overflow-y: auto;width: 190px;}.hideli{padding:5px;}.hideli:hover{color:#090;background-color:white;padding:5px;cursor:pointer}.hidename{font-weight:bold;font-size:13px;text-align:center;background: url(/images/block_bg.png) no-repeat -199px 0;background-size: auto 92%;height: 37px;width: 190px;padding-top:5px}</style><div class="hidename">Скрытые релизы</div><ul id="hided"></ul>')
	$('.entryLink').each(function(i){
		if($(this).text()=="Просьба снять AdBlock"){$(this).closest("div[id^='entryID']").hide()}
		if(getCookie('del'+i)){
			var gsid=getCookie('del'+i).split('_')[0]
			var gname=unescape(getCookie('del'+i).split('_')[1])
			$('#'+gsid).hide()
			$('#hided').append('<div class="hr_v3" style="margin: 2px 0;"></div><li data-id="'+gsid+'" data-i="'+i+'" class="hideli" title="Нажмите чтобы показать блок на сайте">'+gname+'</li>')
			
		}
		$('.viewn_cont').eq(i).append("<style>#lnks"+i+" a{display:block;color:green;}#lnks"+i+"{max-height: 385px;overflow-y: auto;width: 365px;}div[id^='lnks'] br{display:none;}.viewn_title{height:20px!important}</style><div id='lnks"+i+"' style='display:block;margin:0 auto;text-align:center;color:darkred;font-size:20px'></div>")
		$('#lnks'+i).closest('div.viewn_loop').find('h4.viewn_title').append('<span id="spanload'+i+'" title="Нажмите чтобы проверить можно ли скачать серию или нет" style="color:darkred"> <img id="imgload'+i+'" style="cursor:pointer" src="'+lookimg+'" /></span>')
		$('#spanload'+i).after('<img src='+redcrossimage+' title="Удалить сериал из списка на странице (Внимание! Сериал вернётся обратно после перезапуска браузера)" id="reddel'+i+'" style="position: absolute;right: 45px;width: 120px;top: 200px;cursor:pointer"/>')
		var self=$(this)
		$('#reddel'+i).on('click',function(){
			var sid=$(this).closest('div[id^="entryID"]').attr('id')
			var name=$(this).closest('div[id^="entryID"]').find('a.entryLink').eq(0).text()
			setCookie('del'+i,sid+'_'+name,null)
			$(this).attr('src',loadingimg)
			$(this).closest('div[id^="entryID"]').hide()
			$(this).attr('src',redcrossimage)
			$('#hided').append('<div class="hr_v3" style="margin: 2px 0;"></div><li data-id="'+sid+'" data-i="'+i+'" class="hideli" title="Нажмите чтобы показать блок на сайте">'+name+'</li>')
			setclick()
		})
		
		$('#imgload'+i).click(function(){
			getter()
		})
		function getter(){
			$('#imgload'+i).unbind('click')
			$('#imgload'+i).attr('src',loadingimg)
			$('#lnks'+i).text('Идёт загрузка, пожалуйста подождите...')
			$.get(self.attr('href'),function(data){
				var ah=$('.eMessage',data).find('a');
				if(ah.attr('href')!="undefined"){
					var ahtxt=ah.closest('span')
					$('#spanload'+i).attr('style','color:green').text(' Серия выложена')
					.append('<img id="imgload'+i+'" style="cursor:pointer" src="'+lookimg+'" />').parent().parent().find('a').attr('style','color:darkred')
					$('#imgload'+i).click(function(){getter()});
					$('#lnks'+i).html(ahtxt);
				}
				if(ah.attr('href')===undefined || ah.attr('href').indexOf("coldfilm.ru/index")!=-1){
					/*$('#lnks'+i).closest('div[id^="entryID"]').remove()*/
					//$('#lnks'+i).closest('div.viewn_c').hide()
					$('#spanload'+i).attr('style','color:darkred').text(' Серия ещё не выложена ').append('<img id="imgload'+i+'" style="cursor:pointer" src="'+lookimg+'" />').parent().parent().find('a').attr('style','color:darkred')
					$('#imgload'+i).click(function(){getter()});
					$('#lnks'+i).text('Серия ещё не выложена')
					
				}
			})
		}
	})
	setclick()
});		
function setclick(){
	$('#hided').find('li').on('click',function(){
		$('div#'+$(this).data("id")).show();
		setCookie('del'+$(this).data("i"),1,0)
		$('li[data-id="'+$(this).attr("data-id")+'"]').prev().remove();
		$('li[data-id="'+$(this).attr("data-id")+'"]').remove();
	});
}
function getCookie(b){b+="=";for(var d=document.cookie.split(";"),c=0;c<d.length;c++){for(var a=d[c];" "==a.charAt(0);)a=a.substring(1);if(0==a.indexOf(b))return a.substring(b.length,a.length)}return""}function setCookie(b,d,c){var a=new Date;a.setDate(a.getDate()+c);document.cookie=b+"="+escape(d)+(null==c?"":";expires="+a.toUTCString())};
var lookimg="data:image/png;base64,%3D"

var loadingimg="data:image/gif;base64,%3D%3D"

var redcrossimage=" data:image/png;base64,"